<?php

namespace AppBundle\Entity\Article\Repository;

use AppBundle\Entity\Article\Article;
use AppBundle\Entity\Article\Category;
use Doctrine\ORM\EntityRepository;

/**
 * ArticleRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ArticleRepository extends EntityRepository
{
    /**
     * @return array
     */
    public function findByActive()
    {
        $queryBuilder = $this
            ->createQueryBuilder('a')
            ->Where('a.active = 1')
            ->orderBy('a.date', 'DESC');

        return $queryBuilder
            ->getQuery()
            ->getResult();
    }

    /**
     * @param $categoryId
     * @return array
     */
    public function findByCategoryId($categoryId)
    {
        return $this
            ->createQueryBuilder('a')
            ->innerJoin('a.categories', 'ca')
            ->where('ca.id = :categoryId')
            ->andWhere('a.active = :active')
            ->orderBy('a.date', 'DESC')
            ->setParameter(':categoryId', $categoryId)
            ->setParameter(':active', true)
            ->getQuery()
            ->getResult();
    }

    /**
     * @param $term
     * @param $categories
     * @param $dateStart
     * @param $dateEnd
     * @return array
     */
    public function findByTerm($term, $categories, $dateStart, $dateEnd)
    {
        return $this
            ->createQueryBuilder('a')
            ->innerJoin('a.categories', 'ca')
            ->where('ca.id = :categories')
            ->andwhere('a.content LIKE :term')
            ->andWhere('a.active = :active')
            ->andWhere('a.date BETWEEN :dateStart AND :dateEnd')
            ->setParameter(':categories', $categories)
            ->setParameter(':term', '%' . $term . '%')
            ->setParameter(':active', true)
            ->setParameter(':dateStart', $dateStart)
            ->setParameter(':dateEnd', $dateEnd)
            ->getQuery()
            ->getResult();
    }
}